// Define keys to strip
const stripReportKeys = ["id", "business_id", "audit_id", "task_group_id", "created_at"];
const stripQuestionaireKeys = ["id", "report_id"];

// Access report from specific node
const reportRaw = $('Get OutboundCall Report done During this Audit').first().json;

// Access questionaire list correctly from all items
const questionaireItems = $('Get OutboundCall Questionaire done During this Audit').all().map(i => i.json);

// Guard clauses
if (!reportRaw) {
  throw new Error("❌ Report node returned no data.");
}
if (!Array.isArray(questionaireItems)) {
  throw new Error("❌ Questionaire node did not return an array.");
}

// Strip report keys
const report = Object.fromEntries(
  Object.entries(reportRaw).filter(([key]) => !stripReportKeys.includes(key))
);

// Clean each questionaire item
const cleanedQuestionaire = questionaireItems.map(q =>
  Object.fromEntries(Object.entries(q).filter(([key]) => !stripQuestionaireKeys.includes(key)))
);

// Merge into final output
report.questionaire = cleanedQuestionaire;

return [{ json: report }];
