// Enhanced website data extraction with OpenAI analysis
const input = $input.first()?.json || {};

console.log("=== ENHANCED WEBSITE DATA EXTRACTION ===");
console.log("Input structure:", Object.keys(input));

// Extract raw HTML content
let rawHtml = '';

if (input.website_data && Array.isArray(input.website_data)) {
    rawHtml = input.website_data.join(' ');
} else if (typeof input.website_data === 'string') {
    rawHtml = input.website_data;
} else if (input.Services && Array.isArray(input.Services)) {
    // Fallback for existing "Services" key
    rawHtml = input.Services.join(' ');
} else if (typeof input.Services === 'string') {
    rawHtml = input.Services;
} else {
    // Try to find the largest text content
    const allValues = Object.values(input);
    for (const value of allValues) {
        if (typeof value === 'string' && value.length > 100) {
            rawHtml = value;
            break;
        }
    }
}

console.log("Raw HTML length:", rawHtml.length);

// Clean HTML for better analysis (remove excess whitespace and scripts)
const cleanedHtml = rawHtml
    // Remove script and style tags entirely
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')

    // Remove comments
    .replace(/<!--[\s\S]*?-->/g, '')

    // Normalize whitespace
    .replace(/\s+/g, ' ')
    .trim();

console.log("Cleaned HTML length:", cleanedHtml.length);

// Prepare for OpenAI analysis - truncate if too long (keep under 100K chars)
const maxLength = 100000;
let analysisContent = cleanedHtml;

if (analysisContent.length > maxLength) {
    // Try to keep the most important parts
    const parts = analysisContent.split(/(?=<(?:header|main|section|div[^>]*class[^>]*(?:content|about|service|team))|<h[1-6])/i);

    let truncatedContent = '';
    for (const part of parts) {
        if (truncatedContent.length + part.length <= maxLength) {
            truncatedContent += part;
        } else {
            break;
        }
    }

    analysisContent = truncatedContent || analysisContent.substring(0, maxLength);
    console.log("Content truncated to:", analysisContent.length, "characters");
}

// Create the analysis prompt
const analysisPrompt = `Analyze the provided HTML content and extract the following information. Present the output as a JSON object with the specified structure.

**JSON Structure:**
\`\`\`json
{
  "business_owner": {
    "name": null,
    "description": null
  },
  "services_promoted": [],
  "external_marketing_services": [],
  "social_media_profiles": []
}
\`\`\`

**Extraction Details:**

**business_owner** (object or null):
- **name** (string or null): Identify the name of the primary business owner, founder, director, principal, or key responsible individual (e.g., "Dr. John Smith", "Coach Jane Doe", "Founder Alex Lee"). Look for names explicitly associated with leadership roles in "About Us", "Meet Our Team", "Medical Director", or similar sections. If found, provide the full name. If not explicitly found, return null.
- **description** (string or null): If a name is found, look for a concise description about this individual's role, philosophy, or key contributions. Extract a coherent paragraph or sentences directly related to them. If no description is found, return null.

**services_promoted** (array of strings): Identify distinct services, classes, programs, memberships, treatments, or offerings. Look in navigation menus, service sections, or detailed descriptions. Prioritize specific service names over general categories.

**external_marketing_services** (array of objects): Identify third-party services for:
- Lead Management/Generation (pop-ups, chat widgets, newsletter sign-ups)
- Scheduling/Appointments (booking systems, appointment forms, call tracking)
- Payments Processing (payment plans like Cherry, CareCredit, rewards like Aspire, Alle)
For each service: {"name": "ServiceName", "purpose": ["Lead Management"|"Scheduling"|"Payments"], "url": "optional_base_url"}

**social_media_profiles** (array of objects): Find direct links to official social media profiles. For each: {"platform": "PlatformName", "url": "full_profile_url"}

**HTML Content:**
${analysisContent}`;

// Return the prepared data for OpenAI analysis
return [{
    json: {
        htmlContent: analysisContent,
        analysisPrompt: analysisPrompt,
        originalLength: rawHtml.length,
        cleanedLength: cleanedHtml.length,
        analysisLength: analysisContent.length,
        truncated: analysisContent.length < cleanedHtml.length,

        // Legacy fields for compatibility
        fullText: analysisContent.substring(0, 1000) + "...", // First 1000 chars for compatibility
        targetLength: analysisContent.length,
        compressionRatio: ((rawHtml.length - analysisContent.length) / rawHtml.length * 100).toFixed(1) + "%"
    }
}];

// Essential JSON extraction from website analysis OpenAI response
const input = $input.first()?.json || {};

console.log("=== EXTRACTING JSON FROM WEBSITE ANALYSIS ===");

let parsedData = null;

// Extract content from OpenAI response
if (input.message && input.message.content) {
    const content = input.message.content;
    console.log("Found OpenAI content, length:", content.length);

    // Extract JSON from code blocks
    const jsonMatch = content.match(/```json\s*([\s\S]*?)\s*```/);
    if (jsonMatch && jsonMatch[1]) {
        const jsonContent = jsonMatch[1].trim();
        console.log("✅ Extracted JSON from code blocks");

        try {
            parsedData = JSON.parse(jsonContent);
            console.log("✅ Successfully parsed JSON");
            console.log("Business Owner Name:", parsedData.business_owner?.name || "NOT FOUND");
            console.log("Services Count:", parsedData.services_promoted?.length || 0);
            console.log("External Services Count:", parsedData.external_marketing_services?.length || 0);
            console.log("Social Media Count:", parsedData.social_media_profiles?.length || 0);

            // Log social media specifically
            if (parsedData.social_media_profiles && parsedData.social_media_profiles.length > 0) {
                console.log("Social Media Platforms:", parsedData.social_media_profiles.map(p => p.platform).join(', '));
            }

        } catch (error) {
            console.log("❌ JSON parsing error:", error.message);
        }
    } else {
        console.log("❌ No JSON code blocks found");
        console.log("Content preview:", content.substring(0, 200) + "...");
    }
} else {
    console.log("❌ No OpenAI content found");
}

// Return the extracted data - this is what goes to the merge node
return [{
    json: {
        websiteData: parsedData || {
           business_owner: { name: null, description: null },  // ✅ Object format
            services_promoted: [],
            external_marketing_services: [],
            social_media_profiles: []
        },
        extractionSuccess: !!parsedData,
        extractedAt: new Date().toISOString()
    }
}];

const rawOutputItem = $input.first().json;

const rawOutputText = rawOutputItem.candidates[0].content.parts[0].text;

let jsonString;

// Match JSON inside ```json ... ``` or ``` ... ```
