// Get the workflow data
const workflow = $input.item.json;

// Clean the workflow name for display
const workflowName = (workflow.name || 'unnamed')
  .replace(/\//g, '-')
  .replace(/\\/g, '-')
  .replace(/:/g, '-');

const workflowId = workflow.id || 'unknown';

// âœ… FIX: Use ONLY the ID for the filename (stable, no duplicates)
const fileName = `${workflowId}.json`;
const filePath = `backups/top-rated-coach-ai/${fileName}`;

// Create the workflow JSON string with proper formatting
const workflowJson = JSON.stringify(workflow, null, 2);

// Convert to Base64 for GitHub API
const encodedContent = Buffer.from(workflowJson, 'utf-8').toString('base64');

// Create commit message with name for human readability
const currentDate = new Date().toISOString().replace('T', ' ').substring(0, 19);
const commitMessage = `Backup: ${workflowName} (ID: ${workflowId}) - ${currentDate}`;

// Return the prepared data
return {
  workflowName,
  workflowId,
  fileName,
  filePath,
  content: encodedContent,
  commitMessage,
  backupDate: currentDate,
  active: workflow.active || false,
  tags: workflow.tags || []
};

